import UserModal from './modules/UserModal';
import PasswordModal from './modules/PasswordModal';
import { putAction, getFileAccessHttpUrl } from '@/api/manage';
import { frozenBatch } from '@/api/api';
import { JeecgListMixin } from '@/mixins/JeecgListMixin';
import SysUserAgentModal from "./modules/SysUserAgentModal";
import JInput from '@/components/jeecg/JInput';
import UserRecycleBinModal from './modules/UserRecycleBinModal';
export default {
  name: "UserList",
  mixins: [JeecgListMixin],
  components: {
    SysUserAgentModal: SysUserAgentModal,
    UserModal: UserModal,
    PasswordModal: PasswordModal,
    JInput: JInput,
    UserRecycleBinModal: UserRecycleBinModal
  },
  data: function data() {
    return {
      description: '这是用户管理页面',
      queryParam: {},
      recycleBinVisible: false,
      columns: [
      /*{
        title: '#',
        dataIndex: '',
        key:'rowIndex',
        width:60,
        align:"center",
        customRender:function (t,r,index) {
          return parseInt(index)+1;
        }
      },*/
      {
        title: '用户账号',
        align: "center",
        dataIndex: 'username',
        width: 120,
        sorter: true
      }, {
        title: '用户姓名',
        align: "center",
        width: 100,
        dataIndex: 'realname'
      }, {
        title: '头像',
        align: "center",
        width: 120,
        dataIndex: 'avatar',
        scopedSlots: {
          customRender: "avatarslot"
        }
      }, {
        title: '性别',
        align: "center",
        width: 80,
        dataIndex: 'sex_dictText',
        sorter: true
      }, {
        title: '生日',
        align: "center",
        width: 100,
        dataIndex: 'birthday'
      }, {
        title: '手机号码',
        align: "center",
        width: 100,
        dataIndex: 'phone'
      }, {
        title: '部门',
        align: "center",
        width: 180,
        dataIndex: 'orgCode'
      }, {
        title: '负责部门',
        align: "center",
        width: 180,
        dataIndex: 'departIds_dictText'
      }, {
        title: '状态',
        align: "center",
        width: 80,
        dataIndex: 'status_dictText'
      }, {
        title: '操作',
        dataIndex: 'action',
        scopedSlots: {
          customRender: 'action'
        },
        align: "center",
        width: 170
      }],
      url: {
        syncUser: "/process/extActProcess/doSyncUser",
        list: "/sys/user/list",
        delete: "/sys/user/delete",
        deleteBatch: "/sys/user/deleteBatch",
        exportXlsUrl: "/sys/user/exportXls",
        importExcelUrl: "sys/user/importExcel"
      }
    };
  },
  computed: {
    importExcelUrl: function importExcelUrl() {
      return "".concat(window._CONFIG['domianURL'], "/").concat(this.url.importExcelUrl);
    }
  },
  methods: {
    getAvatarView: function getAvatarView(avatar) {
      return getFileAccessHttpUrl(avatar);
    },
    batchFrozen: function batchFrozen(status) {
      if (this.selectedRowKeys.length <= 0) {
        this.$message.warning('请选择一条记录！');
        return false;
      } else {
        var ids = "";
        var that = this;
        var isAdmin = false;
        that.selectionRows.forEach(function (row) {
          if (row.username == 'admin') {
            isAdmin = true;
          }
        });

        if (isAdmin) {
          that.$message.warning('管理员账号不允许此操作,请重新选择！');
          return;
        }

        that.selectedRowKeys.forEach(function (val) {
          ids += val + ",";
        });
        that.$confirm({
          title: "确认操作",
          content: "是否" + (status == 1 ? "解冻" : "冻结") + "选中账号?",
          onOk: function onOk() {
            frozenBatch({
              ids: ids,
              status: status
            }).then(function (res) {
              if (res.success) {
                that.$message.success(res.message);
                that.loadData();
                that.onClearSelected();
              } else {
                that.$message.warning(res.message);
              }
            });
          }
        });
      }
    },
    handleMenuClick: function handleMenuClick(e) {
      if (e.key == 1) {
        this.batchDel();
      } else if (e.key == 2) {
        this.batchFrozen(2);
      } else if (e.key == 3) {
        this.batchFrozen(1);
      }
    },
    handleFrozen: function handleFrozen(id, status, username) {
      var that = this; //TODO 后台校验管理员角色

      if ('admin' == username) {
        that.$message.warning('管理员账号不允许此操作！');
        return;
      }

      frozenBatch({
        ids: id,
        status: status
      }).then(function (res) {
        if (res.success) {
          that.$message.success(res.message);
          that.loadData();
        } else {
          that.$message.warning(res.message);
        }
      });
    },
    handleChangePassword: function handleChangePassword(username) {
      this.$refs.passwordmodal.show(username);
    },
    handleAgentSettings: function handleAgentSettings(username) {
      this.$refs.sysUserAgentModal.agentSettings(username);
      this.$refs.sysUserAgentModal.title = "用户代理人设置";
    },
    passwordModalOk: function passwordModalOk() {//TODO 密码修改完成 不需要刷新页面，可以把datasource中的数据更新一下
    }
  }
};
