  title: '菜单名称',
  dataIndex: 'name',
  key: 'name'
}, {
  title: '菜单类型',
  dataIndex: 'menuType',
  key: 'menuType',
  customRender: function customRender(text) {
    if (text == 0) {
      return '菜单';
    } else if (text == 1) {
      return '菜单';
    } else if (text == 2) {
      return '按钮/权限';
    } else {
      return text;
    }
  }
},
/*{
