{
  title: 'icon',
  dataIndex: 'icon',
  key: 'icon'
}, {
  title: '组件',
  dataIndex: 'component',
  key: 'component',
  scopedSlots: {
    customRender: 'component'
  }
}, {
  title: '路径',
  dataIndex: 'url',
  key: 'url',
  scopedSlots: {
    customRender: 'url'
  }
}, {
  title: '排序',
  dataIndex: 'sortNo',
  key: 'sortNo'
}, {
  title: '操作',
  dataIndex: 'action',
  scopedSlots: {
    customRender: 'action'
  },
