  width: 150
}];
export default {
  name: 'PermissionListAsync',
  mixins: [JeecgListMixin],
  components: {
    PermissionDataRuleList: PermissionDataRuleList,
    PermissionModal: PermissionModal,
    JEllipsis: JEllipsis
  },
  data: function data() {
    return {
      description: '这是菜单管理页面',
      // 表头
      columns: columns,
      loading: false,
      // 展开的行，受控属性
      expandedRowKeys: [],
      url: {
        list: '/sys/permission/list',
        delete: '/sys/permission/delete',
        deleteBatch: '/sys/permission/deleteBatch'
      }
    };
  },
  methods: {
    loadData: function loadData() {
      var _this = this;

      this.loading = true;
      getSystemMenuList().then(function (res) {
        if (res.success) {
          _this.dataSource = res.result;
          return _this.loadDataByExpandedRows(_this.dataSource);
        }
      }).finally(function () {
        _this.loading = false;
      });
    },
    expandSubmenu: function expandSubmenu(expanded, record) {
      if (expanded && (!record.children || record.children.length === 0)) {
        getSystemSubmenu({
          parentId: record.id
        }).then(function (res) {
          if (res.success) {
            record.children = res.result;
          }
        });
      }
    },
    // 根据已展开的行查询数据（用于保存后刷新时异步加载子级的数据）
    loadDataByExpandedRows: function loadDataByExpandedRows(dataList) {
      var _this2 = this;

      if (this.expandedRowKeys.length > 0) {
        return getSystemSubmenuBatch({
          parentIds: this.expandedRowKeys.join(',')
        }).then(function (res) {
          if (res.success) {
            var childrenMap = res.result;

            var fn = function fn(list) {
              list.forEach(function (data) {
                if (_this2.expandedRowKeys.includes(data.id)) {
                  data.children = childrenMap[data.id];
                  fn(data.children);
                }
              });
            };

            fn(dataList);
          }
        });
      } else {
        return Promise.resolve();
      }
    },
    // 打开数据规则编辑
    handleDataRule: function handleDataRule(record) {
      this.$refs.PermissionDataRuleList.edit(record);
    },
    handleAddSub: function handleAddSub(record) {
      this.$refs.modalForm.title = "添加子菜单";
      this.$refs.modalForm.localMenuType = 1;
      this.$refs.modalForm.disableSubmit = false;
      this.$refs.modalForm.edit({
        status: '1',
        permsType: '1',
        route: true,
        'parentId': record.id
      });
    },
    handleExpandedRowsChange: function handleExpandedRowsChange(expandedRows) {
      this.expandedRowKeys = expandedRows;
    }
  }
};
