import pick from 'lodash.pick';
import { httpAction, postAction } from '@/api/manage';
import { validateDuplicateValue } from '@/utils/util';
export default {
  name: 'SysDataSourceModal',
  components: {},
  data: function data() {
    var _this = this;

    return {
      title: '操作',
      visible: false,
      model: {},
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      confirmLoading: false,
      form: this.$form.createForm(this),
      validatorRules: {
        code: {
          validateFirst: true,
          rules: [{
            required: true,
            message: '请输入数据源编码!'
          }, {
            validator: function validator(rule, value, callback) {
              var pattern = /^[a-z|A-Z][a-z|A-Z\d_-]{0,}$/;

              if (!pattern.test(value)) {
                callback('编码必须以字母开头，可包含数字、下划线、横杠');
              } else {
                validateDuplicateValue('sys_data_source', 'code', value, _this.model.id, callback);
              }
            }
          }]
        },
        name: {
          rules: [{
            required: true,
            message: '请输入数据源名称!'
          }]
        },
        dbType: {
          rules: [{
            required: true,
            message: '请选择数据库类型!'
          }]
        },
        dbDriver: {
          rules: [{
            required: true,
            message: '请输入驱动类!'
          }]
        },
        dbUrl: {
          rules: [{
            required: true,
            message: '请输入数据源地址!'
          }]
        },
        dbName: {
          rules: [{
            required: true,
            message: '请输入数据库名称!'
          }]
        },
        dbUsername: {
          rules: [{
            required: true,
            message: '请输入用户名!'
          }]
        },
        dbPassword: {
          rules: [{
            required: true,
            message: '请输入密码!'
          }]
        }
      },
      url: {
        add: '/sys/dataSource/add',
        edit: '/sys/dataSource/edit'
      },
      dbDriverMap: {
        // MySQL 数据库
        '1': {
          dbDriver: 'com.mysql.jdbc.Driver'
        },
        // Oracle
        '2': {
          dbDriver: 'oracle.jdbc.OracleDriver'
        },
        // SQLServer 数据库
        '3': {
          dbDriver: 'com.microsoft.sqlserver.jdbc.SQLServerDriver'
        }
      },
      dbUrlMap: {
        // MySQL 数据库
        '1': {
          dbUrl: 'jdbc:mysql://127.0.0.1:3306/jeecg-boot?characterEncoding=UTF-8&useUnicode=true&useSSL=false'
        },
        // Oracle
        '2': {
          dbUrl: 'jdbc:oracle:thin:@127.0.0.1:1521:ORCL'
        },
        // SQLServer 数据库
        '3': {
          dbUrl: 'jdbc:sqlserver://127.0.0.1:1433;SelectMethod=cursor;DatabaseName=jeecgboot'
        }
      }
    };
  },
  created: function created() {},
  methods: {
    add: function add() {
      this.edit({});
    },
    edit: function edit(record) {
      var _this2 = this;

      this.form.resetFields();
      this.model = Object.assign({}, record);
      this.visible = true;
      this.$nextTick(function () {
        _this2.form.setFieldsValue(pick(_this2.model, 'code', 'name', 'remark', 'dbType', 'dbDriver', 'dbUrl', 'dbName', 'dbUsername', 'dbPassword'));
      });
    },
    close: function close() {
      this.$emit('close');
      this.visible = false;
    },
    handleOk: function handleOk() {
      var _this3 = this;

      // 触发表单验证
      this.form.validateFields(function (err, values) {
        if (!err) {
          _this3.confirmLoading = true;
          var formData = Object.assign(_this3.model, values);
          var httpUrl = _this3.url.add,
              method = 'post';

          if (_this3.model.id) {
            httpUrl = _this3.url.edit;
            method = 'put'; // 由于编码的特殊性，所以不能更改

            formData['code'] = undefined;
          }

          httpAction(httpUrl, formData, method).then(function (res) {
            if (res.success) {
              _this3.$message.success(res.message);

              _this3.$emit('ok');

              _this3.close();
            } else {
              _this3.$message.warning(res.message);
            }
          }).finally(function () {
            _this3.confirmLoading = false;
          });
        }
      });
    },
    handleCancel: function handleCancel() {
      this.close();
    },
    // 测试数据源配置是否可以正常连接
    handleTest: function handleTest() {
      var _this4 = this;

      var keys = ['dbType', 'dbDriver', 'dbUrl', 'dbName', 'dbUsername', 'dbPassword']; // 获取以上字段的值，并清除校验状态

      var fieldsValues = this.form.getFieldsValue(keys);
      var setFields = {};
      keys.forEach(function (key) {
        return setFields[key] = {
          value: fieldsValues[key],
          errors: null
        };
      }); // 清除校验状态，目的是可以让错误文字闪烁

      this.form.setFields(setFields); // 重新校验

      this.$nextTick(function () {
        _this4.form.validateFields(keys, function (errors, values) {
          if (!errors) {
            var loading = _this4.$message.loading('连接中……', 0);

            postAction('/online/cgreport/api/testConnection', fieldsValues).then(function (res) {
              if (res.success) {
                _this4.$message.success('连接成功');
              } else throw new Error(res.message);
            }).catch(function (error) {
              _this4.$warning({
                title: '连接失败',
                content: error.message || error
              });
            }).finally(function () {
              return loading();
            });
          }
        });
      });
    },
    // 数据库类型更改时，联动更改数据库驱动
    handleDbTypeChange: function handleDbTypeChange(val) {
      var dbDriver = this.dbDriverMap[val];
      var dbUrl = this.dbUrlMap[val];

      if (dbDriver) {
        this.form.setFieldsValue(dbDriver);
      }

      if (dbUrl) {
        this.form.setFieldsValue(dbUrl);
      }
    }
  }
};
