import pick from 'lodash.pick';
import { httpAction, putAction } from '@/api/manage';
import JDate from '@/components/jeecg/JDate';
import JUpload from '@/components/jeecg/JUpload';
import JDictSelectTag from "@/components/dict/JDictSelectTag";
import { getFormatDate } from "../../utils/util";
export default {
  name: "OtherPayableModal",
  components: {
    JDate: JDate,
    JUpload: JUpload,
    JDictSelectTag: JDictSelectTag
  },
  data: function data() {
    return {
      action: "",
      title: "操作",
      width: '1200px',
      visible: false,
      moreStatus: false,
      form: this.$form.createForm(this),
      model: {},
      spans: {
        labelCol1: {
          span: 2
        },
        wrapperCol1: {
          span: 22
        },
        labelCol2: {
          span: 4
        },
        wrapperCol2: {
          span: 20
        },
        labelCol3: {
          span: 6
        },
        wrapperCol3: {
          span: 18
        },
        labelCol6: {
          span: 12
        },
        wrapperCol6: {
          span: 12
        }
      },
      confirmLoading: false,
      validatorRules: {
        sourceType: {
          rules: []
        },
        sourceId: {
          rules: []
        },
        sourceNo: {
          rules: []
        },
        isAuto: {
          rules: []
        },
        billNo: {
          rules: [{
            required: true,
            message: '请输入单据编号!'
          }]
        },
        billDate: {
          rules: [{
            required: true,
            message: '请输入单据日期!'
          }]
        },
        supplierId: {
          rules: [{
            required: true,
            message: '请输入供应商!'
          }]
        },
        isRubric: {
          rules: []
        },
        amt: {
          rules: [{
            required: true,
            message: '请输入金额!'
          }]
        },
        checkedAmt: {
          rules: []
        },
        attachment: {
          rules: []
        },
        remark: {
          rules: []
        },
        billProcStatus: {
          rules: []
        },
        isApproved: {
          rules: []
        },
        isClosed: {
          rules: []
        },
        isVoided: {
          rules: []
        },
        effectiveTime: {
          rules: []
        },
        approverId: {
          rules: []
        },
        flowId: {
          rules: []
        },
        createTime: {
          rules: []
        },
        createBy: {
          rules: []
        },
        sysOrgCode: {
          rules: []
        },
        updateTime: {
          rules: []
        },
        updateBy: {
          rules: []
        }
      },
      url: {
        add: "/finance/finPayable/add",
        edit: "/finance/finPayable/edit",
        approve: "/finance/finPayable/approve"
      }
    };
  },
  computed: {
    readOnly: function readOnly() {
      return this.action !== "add" && this.action !== "edit";
    }
  },
  methods: {
    add: function add() {
      this.model = {};
      this.model.payableType = '299';
      this.model.isRubric = 0;
      this.model.isAuto = 0; //处理状态：编辑中

      this.model.billProcStatus = '12';
      this.model.checkedAmt = 0; // 请求后台的填值规则接口地址

      var url = '/sys/fillRule/executeRuleByCode/';
      var ruleCode = 'payable_bill_no';
      var that = this;
      putAction(url + ruleCode, {}).then(function (res) {
        // 执行成功，获取返回的值，并赋到页面上
        if (res.success) {
          that.$nextTick(function () {
            that.form.setFieldsValue({
              'billNo': res.result,
              'billDate': getFormatDate()
            });
          });
        }
      });
      this.edit(this.model);
    },
    edit: function edit(record) {
      var _this = this;

      this.form.resetFields();
      this.model = Object.assign({}, record);
      this.visible = true;
      this.$nextTick(function () {
        _this.form.setFieldsValue(pick(_this.model, 'sourceType', 'sourceNo', 'sourceId', 'billNo', 'billDate', 'supplierId', 'isRubric', 'amt', 'checkedAmt', 'attachment', 'remark', 'billProcStatus', 'approverId', 'flowId', 'isApproved', 'effectiveTime', 'isVoided', 'isClosed', 'sysOrgCode', 'createBy', 'createTime', 'updateBy', 'updateTime'));
      });
    },
    close: function close() {
      this.$emit('close');
      this.visible = false;
    },
    popupCallback: function popupCallback(row) {
      this.form.setFieldsValue(pick(row, 'sourceType', 'sourceNo', 'sourceId', 'billNo', 'billDate', 'supplierId', 'isRubric', 'amt', 'checkedAmt', 'attachment', 'remark', 'billProcStatus', 'approverId', 'flowId', 'isApproved', 'effectiveTime', 'isVoided', 'isClosed', 'sysOrgCode', 'createBy', 'createTime', 'updateBy', 'updateTime'));
    },
    handleOk: function handleOk() {
      var _this2 = this;

      var that = this; // 触发表单验证

      this.form.validateFields(function (err, values) {
        if (!err) {
          that.confirmLoading = true;
          var httpurl = '';
          var method = '';

          if (!_this2.model.id) {
            httpurl += _this2.url.add;
            method = 'post';
          } else {
            httpurl += _this2.url.edit;
            method = 'put';
          }

          var formData = Object.assign(_this2.model, values);
          httpAction(httpurl, formData, method).then(function (res) {
            if (res.success) {
              that.$message.success(res.message);
              that.$emit('ok');
            } else {
              that.$message.warning(res.message);
            }
          }).finally(function () {
            that.confirmLoading = false;
            that.close();
          });
        }
      });
    },
    handleCancel: function handleCancel() {
      this.close();
    },
    handleSave: function handleSave() {
      this.handleOk();
    },
    handleSubmit: function handleSubmit() {
      var _this3 = this;

      //处理状态=编辑完成
      this.form.setFieldsValue({
        billProcStatus: '13'
      }); //出错时要恢复billProcStatus为"12"，所以下面未直接调用handleSave()！

      var that = this; // 触发表单验证

      this.form.validateFields(function (err, values) {
        if (!err) {
          that.confirmLoading = true;
          var httpurl = '';
          var method = '';

          if (!_this3.model.id) {
            httpurl += _this3.url.add;
            method = 'post';
          } else {
            httpurl += _this3.url.edit;
            method = 'put';
          }

          var formData = Object.assign(_this3.model, values);
          httpAction(httpurl, formData, method).then(function (res) {
            if (res.success) {
              that.$message.success(res.message);
              that.$emit('ok');
            } else {
              //处理状态=编辑中
              that.form.setFieldsValue({
                billProcStatus: '12'
              });
              that.$message.warning(res.message);
            }
          }).finally(function () {
            that.confirmLoading = false;
            that.close();
          });
        }
      });
    },
    handleApproved: function handleApproved() {
      var that = this;
      putAction(that.url.approve, {
        id: that.model.id
      }).then(function (res) {
        if (res.success) {
          that.$message.success(res.message);
          that.$emit('ok');
          that.close();
        } else {
          that.$message.warning(res.message);
        }
      });
    }
  }
};
