import JUpload from '@/components/jeecg/JUpload';
import { getFileAccessHttpUrl } from '@/api/manage';

var getFileName = function getFileName(path) {
  if (path.lastIndexOf("\\") >= 0) {
    var reg = new RegExp("\\\\", "g");
    path = path.replace(reg, "/");
  }

  return path.substring(path.lastIndexOf("/") + 1);
};

export default {
  name: 'JFilePop',
  components: {
    JUpload: JUpload
  },
  props: {
    title: {
      type: String,
      default: '',
      required: false
    },
    position: {
      type: String,
      default: 'right',
      required: false
    },
    height: {
      type: Number,
      default: 200,
      required: false
    },
    width: {
      type: Number,
      default: 520,
      required: false
    },
    popContainer: {
      type: String,
      default: '',
      required: false
    },
    disabled: {
      type: Boolean,
      default: false,
      required: false
    }
  },
  data: function data() {
    return {
      visible: false,
      filePath: '',
      id: '',
      fileType: 'file'
    };
  },
  methods: {
    handleChange: function handleChange(value) {
      this.filePath = value;
    },
    show: function show(id, value, flag) {
      this.id = id;
      this.filePath = value;
      this.visible = true;

      if (flag === 'img') {
        this.fileType = 'image';
      } else {
        this.fileType = 'file';
      }
    },
    ok: function ok() {
      if (!this.filePath) {
        this.$message.error("未上传任何文件");
        return false;
      }

      var arr = this.filePath.split(",");
      var obj = {
        name: getFileName(arr[0]),
        url: getFileAccessHttpUrl(arr[0]),
        path: this.filePath,
        status: 'done',
        id: this.id
      };
      this.$emit('ok', obj);
      this.visible = false;
    },
    close: function close() {
      this.visible = false;
    }
  }
};
