import Vue from 'vue';
import { ACCESS_TOKEN } from "@/store/mutation-types";
import { getFileAccessHttpUrl } from '@/api/manage';
var FILE_TYPE_ALL = "all";
var FILE_TYPE_IMG = "image";
var FILE_TYPE_TXT = "file";

var uidGenerator = function uidGenerator() {
  return '-' + parseInt(Math.random() * 10000 + 1, 10);
};

var getFileName = function getFileName(path) {
  if (path.lastIndexOf("\\") >= 0) {
    var reg = new RegExp("\\\\", "g");
    path = path.replace(reg, "/");
  }

  return path.substring(path.lastIndexOf("/") + 1);
};

export default {
  name: 'JUpload',
  data: function data() {
    return {
      uploadAction: window._CONFIG['domianURL'] + "/sys/common/upload",
      headers: {},
      fileList: [],
      newFileList: [],
      uploadGoOn: true,
      previewVisible: false,
      //---------------------------- begin 图片左右换位置 -------------------------------------
      previewImage: '',
      containerId: '',
      top: '',
      left: '',
      moveDisplay: 'none',
      showMoverTask: false,
      moverHold: false,
      currentImg: '' //---------------------------- end 图片左右换位置 -------------------------------------

    };
  },
  props: {
    text: {
      type: String,
      required: false,
      default: "点击上传"
    },
    fileType: {
      type: String,
      required: false,
      default: FILE_TYPE_ALL
    },

    /*这个属性用于控制文件上传的业务路径*/
    bizPath: {
      type: String,
      required: false,
      default: "temp"
    },
    value: {
      type: [String, Array],
      required: false
    },
    // update-begin- --- author:wangshuai ------ date:20190929 ---- for:Jupload组件增加是否能够点击
    disabled: {
      type: Boolean,
      required: false,
      default: false
    },
    // update-end- --- author:wangshuai ------ date:20190929 ---- for:Jupload组件增加是否能够点击
    //此属性被废弃了
    triggerChange: {
      type: Boolean,
      required: false,
      default: false
    },

    /**
     * update -- author:lvdandan -- date:20190219 -- for:Jupload组件增加是否返回url，
     * true：仅返回url
     * false：返回fileName filePath fileSize
     */
    returnUrl: {
      type: Boolean,
      required: false,
      default: true
    },
    number: {
      type: Number,
      required: false,
      default: 0
    },
    buttonVisible: {
      type: Boolean,
      required: false,
      default: true
    }
  },
  watch: {
    value: {
      immediate: true,
      handler: function handler() {
        var val = this.value;

        if (val instanceof Array) {
          if (this.returnUrl) {
            this.initFileList(val.join(','));
          } else {
            this.initFileListArr(val);
          }
        } else {
          this.initFileList(val);
        }
      }
    }
  },
  computed: {
    isImageComp: function isImageComp() {
      return this.fileType === FILE_TYPE_IMG;
    },
    complistType: function complistType() {
      return this.fileType === FILE_TYPE_IMG ? 'picture-card' : 'text';
    }
  },
  created: function created() {
    var token = Vue.ls.get(ACCESS_TOKEN); //---------------------------- begin 图片左右换位置 -------------------------------------

    this.headers = {
      "X-Access-Token": token
    };
    this.containerId = 'container-ty-' + new Date().getTime(); //---------------------------- end 图片左右换位置 -------------------------------------
  },
  methods: {
    initFileListArr: function initFileListArr(val) {
      if (!val || val.length == 0) {
        this.fileList = [];
        return;
      }

      var fileList = [];

      for (var a = 0; a < val.length; a++) {
        var url = getFileAccessHttpUrl(val[a].filePath);
        fileList.push({
          uid: uidGenerator(),
          name: val[a].fileName,
          status: 'done',
          url: url,
          response: {
            status: "history",
            message: val[a].filePath
          }
        });
      }

      this.fileList = fileList;
    },
    initFileList: function initFileList(paths) {
      if (!paths || paths.length == 0) {
        //return [];
        // update-begin- --- author:os_chengtgen ------ date:20190729 ---- for:issues:326,Jupload组件初始化bug
        this.fileList = [];
        return; // update-end- --- author:os_chengtgen ------ date:20190729 ---- for:issues:326,Jupload组件初始化bug
      }

      var fileList = [];
      var arr = paths.split(",");

      for (var a = 0; a < arr.length; a++) {
        var url = getFileAccessHttpUrl(arr[a]);
        fileList.push({
          uid: uidGenerator(),
          name: getFileName(arr[a]),
          status: 'done',
          url: url,
          response: {
            status: "history",
            message: arr[a]
          }
        });
      }

      this.fileList = fileList;
    },
    handlePathChange: function handlePathChange() {
      var uploadFiles = this.fileList;
      var path = '';

      if (!uploadFiles || uploadFiles.length == 0) {
        path = '';
      }

      var arr = [];

      for (var a = 0; a < uploadFiles.length; a++) {
        arr.push(uploadFiles[a].response.message);
      }

      if (arr.length > 0) {
        path = arr.join(",");
      }

      this.$emit('change', path);
    },
    beforeUpload: function beforeUpload(file) {
      this.uploadGoOn = true;
      var fileType = file.type;

      if (this.fileType === FILE_TYPE_IMG) {
        if (fileType.indexOf('image') < 0) {
          this.$message.warning('请上传图片');
          this.uploadGoOn = false;
          return false;
        }
      } //TODO 扩展功能验证文件大小


      return true;
    },
    handleChange: function handleChange(info) {
      console.log("--文件列表改变--");

      if (!info.file.status && this.uploadGoOn === false) {
        info.fileList.pop();
      }

      var fileList = info.fileList;

      if (info.file.status === 'done') {
        if (this.number > 0) {
          fileList = fileList.slice(-this.number);
        }

        if (info.file.response.success) {
          fileList = fileList.map(function (file) {
            if (file.response) {
              var reUrl = file.response.message;
              file.url = getFileAccessHttpUrl(reUrl);
            }

            return file;
          });
        } //this.$message.success(`${info.file.name} 上传成功!`);

      } else if (info.file.status === 'error') {
        this.$message.error("".concat(info.file.name, " \u4E0A\u4F20\u5931\u8D25."));
      } else if (info.file.status === 'removed') {
        this.handleDelete(info.file);
      }

      this.fileList = fileList;

      if (info.file.status === 'done' || info.file.status === 'removed') {
        //returnUrl为true时仅返回文件路径
        if (this.returnUrl) {
          this.handlePathChange();
        } else {
          //returnUrl为false时返回文件名称、文件路径及文件大小
          this.newFileList = [];

          for (var a = 0; a < fileList.length; a++) {
            var fileJson = {
              fileName: fileList[a].name,
              filePath: fileList[a].response.message,
              fileSize: fileList[a].size
            };
            this.newFileList.push(fileJson);
          }

          this.$emit('change', this.newFileList);
        }
      }
    },
    handleDelete: function handleDelete(file) {
      //如有需要新增 删除逻辑
      console.log(file);
    },
    handlePreview: function handlePreview(file) {
      if (this.fileType === FILE_TYPE_IMG) {
        this.previewImage = file.url || file.thumbUrl;
        this.previewVisible = true;
      } else {
        location.href = file.url;
      }
    },
    handleCancel: function handleCancel() {
      this.previewVisible = false;
    },
    //---------------------------- begin 图片左右换位置 -------------------------------------
    moveLast: function moveLast() {
      //console.log(ev)
      //console.log(this.fileList)
      //console.log(this.currentImg)
      var index = this.getIndexByUrl();

      if (index == 0) {
        this.$message.warn('未知的操作');
      } else {
        var curr = this.fileList[index].url;
        var last = this.fileList[index - 1].url;
        var arr = [];

        for (var i = 0; i < this.fileList.length; i++) {
          if (i == index - 1) {
            arr.push(curr);
          } else if (i == index) {
            arr.push(last);
          } else {
            arr.push(this.fileList[i].url);
          }
        }

        this.currentImg = last;
        this.$emit('change', arr.join(','));
      }
    },
    moveNext: function moveNext() {
      var index = this.getIndexByUrl();

      if (index == this.fileList.length - 1) {
        this.$message.warn('已到最后~');
      } else {
        var curr = this.fileList[index].url;
        var next = this.fileList[index + 1].url;
        var arr = [];

        for (var i = 0; i < this.fileList.length; i++) {
          if (i == index + 1) {
            arr.push(curr);
          } else if (i == index) {
            arr.push(next);
          } else {
            arr.push(this.fileList[i].url);
          }
        }

        this.currentImg = next;
        this.$emit('change', arr.join(','));
      }
    },
    getIndexByUrl: function getIndexByUrl() {
      for (var i = 0; i < this.fileList.length; i++) {
        if (this.fileList[i].url === this.currentImg || encodeURI(this.fileList[i].url) === this.currentImg) {
          return i;
        }
      }

      return -1;
    }
  },
  mounted: function mounted() {
    var _this = this;

    var moverObj = document.getElementById(this.containerId + '-mover');
    moverObj.addEventListener('mouseover', function () {
      _this.moverHold = true;
      _this.moveDisplay = 'block';
    });
    moverObj.addEventListener('mouseout', function () {
      _this.moverHold = false;
      _this.moveDisplay = 'none';
    });
    var picList = document.getElementById(this.containerId) ? document.getElementById(this.containerId).getElementsByClassName('ant-upload-list-picture-card') : [];

    if (picList && picList.length > 0) {
      picList[0].addEventListener('mouseover', function (ev) {
        ev = ev || window.event;
        var target = ev.target || ev.srcElement;

        if ('ant-upload-list-item-info' == target.className) {
          _this.showMoverTask = false;
          var item = target.parentElement;
          _this.left = item.offsetLeft;
          _this.top = item.offsetTop + item.offsetHeight - 50;
          _this.moveDisplay = 'block';
          _this.currentImg = target.getElementsByTagName('img')[0].src;
        }
      });
      picList[0].addEventListener('mouseout', function (ev) {
        ev = ev || window.event;
        var target = ev.target || ev.srcElement; //console.log('移除',target)

        if ('ant-upload-list-item-info' == target.className) {
          _this.showMoverTask = true;
          setTimeout(function () {
            if (_this.moverHold === false) _this.moveDisplay = 'none';
          }, 100);
        }

        if ('ant-upload-list-item ant-upload-list-item-done' == target.className || 'ant-upload-list ant-upload-list-picture-card' == target.className) {
          _this.moveDisplay = 'none';
        }
      }); //---------------------------- end 图片左右换位置 -------------------------------------
    }
  },
  model: {
    prop: 'value',
    event: 'change'
  }
};
