import { httpAction } from '@/api/manage';
import pick from 'lodash.pick';
import JDate from '@/components/jeecg/JDate';
import JDictSelectTag from "@/components/dict/JDictSelectTag";
import JTreeSelect from '@/components/jeecg/JTreeSelect';
export default {
  name: "BasMaterialModal",
  components: {
    JDate: JDate,
    JDictSelectTag: JDictSelectTag,
    JTreeSelect: JTreeSelect
  },
  data: function data() {
    return {
      action: "",
      form: this.$form.createForm(this),
      title: "操作",
      width: 600,
      visible: false,
      model: {},
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 6
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 17
        }
      },
      confirmLoading: false,
      validatorRules: {
        code: {
          rules: [{
            required: true,
            message: '请输入编码!'
          }]
        },
        name: {
          rules: [{
            required: true,
            message: '请输入名称!'
          }]
        },
        unitId: {
          rules: [{
            required: true,
            message: '请输入计量单位!'
          }]
        },
        categoryId: {
          rules: [{
            required: true,
            message: '请输入分类!'
          }]
        },
        isEnabled: {
          rules: [{
            required: true,
            message: '请输入是否启用!'
          }]
        },
        salePrice: {
          rules: [{
            pattern: /^(([1-9][0-9]*)|([0]\.\d{0,2}|[1-9][0-9]*\.\d{0,2}))$/,
            message: '请输入正确的金额!'
          }]
        }
      },
      url: {
        add: "/bas/basMaterial/add",
        edit: "/bas/basMaterial/edit"
      }
    };
  },
  computed: {
    readOnly: function readOnly() {
      return this.action !== "add" && this.action !== "edit";
    }
  },
  created: function created() {},
  methods: {
    add: function add() {
      this.edit({});
    },
    edit: function edit(record) {
      var _this = this;

      this.form.resetFields();
      this.model = Object.assign({}, record);
      this.visible = true;
      this.$nextTick(function () {
        _this.form.setFieldsValue(pick(_this.model, 'code', 'name', 'categoryId', 'isEnabled', 'model', 'unitId', 'salePrice', 'taxCode', 'remark', 'createBy', 'createTime', 'updateBy', 'updateTime'));
      });
    },
    close: function close() {
      this.$emit('close');
      this.visible = false;
    },
    handleOk: function handleOk() {
      var _this2 = this;

      var that = this; // 触发表单验证

      this.form.validateFields(function (err, values) {
        if (!err) {
          that.confirmLoading = true;
          var httpurl = '';
          var method = '';

          if (!_this2.model.id) {
            httpurl += _this2.url.add;
            method = 'post';
          } else {
            httpurl += _this2.url.edit;
            method = 'put';
          }

          var formData = Object.assign(_this2.model, values);
          console.log("表单提交数据", formData);
          httpAction(httpurl, formData, method).then(function (res) {
            if (res.success) {
              that.$message.success(res.message);
              that.$emit('ok');
            } else {
              that.$message.warning(res.message);
            }
          }).finally(function () {
            that.confirmLoading = false;
            that.close();
          });
        }
      });
    },
    handleCancel: function handleCancel() {
      this.close();
    },
    popupCallback: function popupCallback(row) {
      this.form.setFieldsValue(pick(row, 'code', 'name', 'categoryId', 'isEnabled', 'model', 'unitId', 'salePrice', 'taxCode', 'remark', 'createBy', 'createTime', 'updateBy', 'updateTime'));
    }
  }
};
