import { JeecgListMixin } from '@/mixins/JeecgListMixin';
import { getAction } from '@/api/manage';
import SysDepartRoleModal from './SysDepartRoleModal';
import DeptRoleAuthModal from './DeptRoleAuthModal';
export default {
  name: 'DeptRoleInfo',
  components: {
    DeptRoleAuthModal: DeptRoleAuthModal,
    SysDepartRoleModal: SysDepartRoleModal
  },
  mixins: [JeecgListMixin],
  data: function data() {
    return {
      description: '部门角色信息',
      currentDeptId: '',
      // 表头
      columns: [{
        title: '部门角色名称',
        align: "center",
        dataIndex: 'roleName'
      }, {
        title: '部门角色编码',
        align: "center",
        dataIndex: 'roleCode'
      }, {
        title: '部门',
        align: "center",
        dataIndex: 'departId_dictText'
      }, {
        title: '备注',
        align: "center",
        dataIndex: 'description'
      }, {
        title: '操作',
        dataIndex: 'action',
        scopedSlots: {
          customRender: 'action'
        },
        align: "center",
        width: 170
      }],
      url: {
        list: "/sys/sysDepartRole/list",
        delete: "/sys/sysDepartRole/delete",
        deleteBatch: "/sys/sysDepartRole/deleteBatch"
      }
    };
  },
  created: function created() {},
  methods: {
    searchReset: function searchReset() {
      this.queryParam = {};
      this.loadData(1);
    },
    loadData: function loadData(arg) {
      var _this = this;

      if (!this.url.list) {
        this.$message.error("请设置url.list属性!");
        return;
      } //加载数据 若传入参数1则加载第一页的内容


      if (arg === 1) {
        this.ipagination.current = 1;
      }

      var params = this.getQueryParams(); //查询条件

      params.deptId = this.currentDeptId;
      getAction(this.url.list, params).then(function (res) {
        if (res.success && res.result) {
          _this.dataSource = res.result.records;
          _this.ipagination.total = res.result.total;
        }
      });
    },
    open: function open(record) {
      this.currentDeptId = record.id;
      this.loadData(1);
    },
    clearList: function clearList() {
      this.currentDeptId = '';
      this.dataSource = [];
    },
    hasSelectDept: function hasSelectDept() {
      if (this.currentDeptId == '') {
        this.$message.error("请选择一个部门!");
        return false;
      }

      return true;
    },
    handleEdit: function handleEdit(record) {
      this.$refs.modalForm.title = "编辑";
      this.$refs.modalForm.departDisabled = true;
      this.$refs.modalForm.disableSubmit = false;
      this.$refs.modalForm.edit(record, record.departId);
    },
    handleAdd: function handleAdd() {
      if (this.currentDeptId == '') {
        this.$message.error("请选择一个部门!");
      } else {
        this.$refs.modalForm.departDisabled = true;
        this.$refs.modalForm.add(this.currentDeptId);
        this.$refs.modalForm.title = "新增";
      }
    },
    handlePerssion: function handlePerssion(record) {
      this.$refs.modalDeptRole.show(record.id, record.departId);
    }
  }
};
