import JSelectDepartModal from './modal/JSelectDepartModal';
export default {
  name: 'JSelectDepart',
  components: {
    JSelectDepartModal: JSelectDepartModal
  },
  props: {
    modalWidth: {
      type: Number,
      default: 500,
      required: false
    },
    multi: {
      type: Boolean,
      default: false,
      required: false
    },
    rootOpened: {
      type: Boolean,
      default: true,
      required: false
    },
    value: {
      type: String,
      required: false
    },
    disabled: {
      type: Boolean,
      required: false,
      default: false
    },
    // 自定义返回字段，默认返回 id
    customReturnField: {
      type: String,
      default: 'id'
    }
  },
  data: function data() {
    return {
      visible: false,
      confirmLoading: false,
      departNames: "",
      departIds: ''
    };
  },
  mounted: function mounted() {
    this.departIds = this.value;
  },
  watch: {
    value: function value(val) {
      if (this.customReturnField === 'id') {
        this.departIds = val;
      }
    }
  },
  methods: {
    initComp: function initComp(departNames) {
      this.departNames = departNames;
    },
    openModal: function openModal() {
      this.$refs.innerDepartSelectModal.show();
    },
    handleOK: function handleOK(rows, idstr) {
      var _this = this;

      var value = '';

      if (!rows && rows.length <= 0) {
        this.departNames = '';
        this.departIds = '';
      } else {
        value = rows.map(function (row) {
          return row[_this.customReturnField];
        }).join(',');
        this.departNames = rows.map(function (row) {
          return row['departName'];
        }).join(',');
        this.departIds = idstr;
      }

      this.$emit("change", value);
    },
    getDepartNames: function getDepartNames() {
      return this.departNames;
    },
    handleEmpty: function handleEmpty() {
      this.handleOK('');
    }
  },
  model: {
    prop: 'value',
    event: 'change'
  }
};
