          title:'名称22',
          fixed:"left",
          align:"left",
          dataIndex: 'name'
        },
        {
          title:'编码22',
          align:"center",
          dataIndex: 'code',
          scopedSlots: { customRender: 'code' }
        },
        {
          title:'分类',
          align:"left",
          dataIndex: 'categoryId_dictText'
        },
        {
          title:'规格型号',
          align:"left",
          dataIndex: 'model'
