import pick from 'lodash.pick'
import { httpAction, putAction } from '@/api/manage'
import JDate from '@/components/jeecg/JDate'
import JUpload from '@/components/jeecg/JUpload'
import JDictSelectTag from "@/components/dict/JDictSelectTag"
import {getFormatDate} from "../../utils/util";

export default {
  name: "OtherPayableModal",
  components: {
    JDate,
    JUpload,
    JDictSelectTag,
  },
  data () {
    return {
      action: "",
      title:"操作",
      width: '1200px',
      visible: false,
      moreStatus: false,
      form: this.$form.createForm(this),
      model: {},

      spans: {
        labelCol1: { span: 2 },
        wrapperCol1: { span: 22 },
        labelCol2: { span: 4 },
        wrapperCol2: { span: 20 },
        labelCol3: { span: 6 },
        wrapperCol3: { span: 18 },
        labelCol6: { span: 12 },
        wrapperCol6: { span: 12 }
      },

      confirmLoading: false,
      validatorRules: {
        sourceType: {rules: []},
        sourceId: {rules: []},
        sourceNo:  {rules: []},
        isAuto: {rules: []},
        billNo: {rules: [
          {required: true, message: '请输入单据编号!'},
        ]},
        billDate: {rules: [
          {required: true, message: '请输入单据日期!'},
        ]},
        supplierId: {rules: [
          {required: true, message: '请输入供应商!'},
        ]},
        isRubric: {rules: []},
        amt: {rules: [
          {required: true, message: '请输入金额!'},
        ]},
        checkedAmt:  {rules: []},
        attachment: {rules: []},
        remark: {rules: []},
        billProcStatus: {rules: []},
        isApproved: {rules: []},
        isClosed: {rules: []},
        isVoided: {rules: []},
        effectiveTime: {rules: []},
        approverId: {rules: []},
        flowId: {rules: []},
        createTime: {rules: []},
        createBy: {rules: []},
        sysOrgCode: {rules: []},
        updateTime: {rules: []},
        updateBy: {rules: []},
     },
      url: {
        add: "/finance/finPayable/add",
        edit: "/finance/finPayable/edit",
        approve: "/finance/finPayable/approve",
      }
    }
  },

  computed: {
    readOnly: function() {
      return this.action !== "add" && this.action !== "edit"
    }
  },

  methods: {
    add () {
      this.model = {}
      this.model.payableType = '299'
      this.model.isRubric = 0;
      this.model.isAuto = 0
      //处理状态：编辑中
      this.model.billProcStatus = '12'
      this.model.checkedAmt = 0;

      // 请求后台的填值规则接口地址
      const url = '/sys/fillRule/executeRuleByCode/'
      const ruleCode = 'payable_bill_no'
      const that = this
      putAction(url + ruleCode, {}).then(res => {
        // 执行成功，获取返回的值，并赋到页面上
        if (res.success) {
          that.$nextTick(() => {
            that.form.setFieldsValue({'billNo':res.result, 'billDate':getFormatDate()})
          })
        }
      })


      this.edit(this.model);
    },
    edit (record) {
      this.form.resetFields();
      this.model = Object.assign({}, record);
      this.visible = true;
      this.$nextTick(() => {
        this.form.setFieldsValue(pick(this.model,'sourceType','sourceNo','sourceId','billNo','billDate','supplierId','isRubric','amt','checkedAmt','attachment','remark','billProcStatus','approverId','flowId','isApproved','effectiveTime','isVoided','isClosed','sysOrgCode','createBy','createTime','updateBy','updateTime'))
      })
    },
    close () {
      this.$emit('close');
      this.visible = false;
    },
    popupCallback(row){
      this.form.setFieldsValue(pick(row,'sourceType','sourceNo','sourceId','billNo','billDate','supplierId','isRubric','amt','checkedAmt','attachment','remark','billProcStatus','approverId','flowId','isApproved','effectiveTime','isVoided','isClosed','sysOrgCode','createBy','createTime','updateBy','updateTime'))
    },

    handleOk () {
      const that = this;
      // 触发表单验证
      this.form.validateFields((err, values) => {
        if (!err) {
          that.confirmLoading = true;
          let httpurl = '';
          let method = '';
          if(!this.model.id){
            httpurl+=this.url.add;
            method = 'post';
          }else{
            httpurl+=this.url.edit;
             method = 'put';
          }
          let formData = Object.assign(this.model, values);
          httpAction(httpurl,formData,method).then((res)=>{
            if(res.success){
              that.$message.success(res.message);
              that.$emit('ok');
            }else{
              that.$message.warning(res.message);
            }
          }).finally(() => {
            that.confirmLoading = false;
            that.close();
          })
        }
       })
    },
    handleCancel () {
      this.close()
    },

    handleSave() {
      this.handleOk()
    },

    handleSubmit() {
      //处理状态=编辑完成
      this.form.setFieldsValue({billProcStatus: '13'});

      //出错时要恢复billProcStatus为"12"，所以下面未直接调用handleSave()！
      const that = this;
      // 触发表单验证
      this.form.validateFields((err, values) => {
        if (!err) {
          that.confirmLoading = true;
          let httpurl = '';
          let method = '';
          if(!this.model.id){
            httpurl+=this.url.add;
            method = 'post';
          }else{
            httpurl+=this.url.edit;
            method = 'put';
          }
          let formData = Object.assign(this.model, values);
          httpAction(httpurl,formData,method).then((res)=>{
            if(res.success){
              that.$message.success(res.message);
              that.$emit('ok');
            }else{
              //处理状态=编辑中
              that.form.setFieldsValue({billProcStatus:'12'});
              that.$message.warning(res.message);
            }
          }).finally(() => {
            that.confirmLoading = false;
            that.close();
          })
        }
      })
   },

    handleApproved() {
      const that = this;
      putAction(that.url.approve, {id: that.model.id}).then((res) => {
        if (res.success) {
          that.$message.success(res.message);
          that.$emit('ok')
          that.close()
        } else {
          that.$message.warning(res.message);
        }
      })
    },

  }
}
